mapper.select:
SELECT p.fecha_emision AS fechaEmision, p.tipo_comprobante AS tipoComprobante, p.serie, p.numero, p.tip_comprob_afectado AS tipoComprobanteAfectado, p.serie_afectado AS serieAfectado, p.numero_afectado AS numeroAfectado, p.num_doc_ident_receptor AS numDocIdentReceptor, p.denominacion_receptor AS denominacionReceptor, p.codigo_moneda AS codigoMoneda, ROUND(p.monto_imp_total_venta, 2) AS montoImporteTotalVenta, p.estado, p.fecha_registro AS fechaRegistro, p.ruc_emisor AS rucEmisor, p.estado_sunat AS estadoSunat, p.identificador_documento AS identificadorBaja, p.email_receptor AS emailReceptor, p.id_payment_voucher AS idPaymentVoucher, (t.serie || '-' || t.numero) AS sernumguia, p.identificador_documento AS identificadorDocumento, p.uuid, p.mensaje_respuesta AS mensajeRespuesta, p.pagado FROM payment_voucher p LEFT JOIN guia_remision t ON t.id_payment_voucher = p.id_payment_voucher WHERE p.ruc_emisor = #{filtro.rucEmisor} AND p.fecha_emision_date BETWEEN #{filtro.filtroDesde} AND #{filtro.filtroHasta}
AND p.tipo_comprobante LIKE #{filtro.filtroTipoComprobante}
AND p.num_doc_ident_receptor LIKE #{filtro.filtroRuc}
AND p.serie LIKE #{filtro.filtroSerie}
AND p.numero = #{filtro.filtroNumero}
AND p.oficina_id = #{filtro.idOficina}
AND p.estado LIKE #{filtro.estadoSunat}
ORDER BY p.fecha_emision_date DESC, p.numero DESC OFFSET #{filtro.numPagina} LIMIT #{filtro.porPagina}

SELECT COUNT(p.id_payment_voucher)
FROM payment_voucher p

    p.ruc_emisor = #{filtro.rucEmisor}
    AND p.fecha_emision_date BETWEEN #{filtro.filtroDesde} AND #{filtro.filtroHasta}


        AND p.tipo_comprobante LIKE CONCAT('%', #{filtro.filtroTipoComprobante}, '%')



        AND p.num_doc_ident_receptor LIKE CONCAT('%', #{filtro.filtroRuc}, '%')



        AND p.serie LIKE CONCAT('%', #{filtro.filtroSerie}, '%')



        AND p.numero = #{filtro.filtroNumero}



        AND p.oficina_id = #{filtro.idOficina}



        AND p.estado LIKE CONCAT('%', #{filtro.estadoSunat}, '%')

SELECT p.codigo_moneda AS codigoMoneda,
COALESCE(
SUM(
CASE p.tipo_comprobante
WHEN '07' THEN p.monto_imp_total_venta * (-1)
ELSE p.monto_imp_total_venta
END
), 0
) AS montoImporteTotalVenta
FROM payment_voucher p
WHERE p.ruc_emisor = #{filtro.rucEmisor}
AND p.fecha_emision_date BETWEEN #{filtro.filtroDesde} AND #{filtro.filtroHasta}


    AND p.tipo_comprobante LIKE #{s}


    AND p.num_doc_ident_receptor LIKE #{filtro.filtroRuc}


    AND p.serie LIKE #{filtro.filtroSerie}


    AND p.numero = #{filtro.filtroNumero}


    AND p.oficina_id = #{filtro.idOficina}


    AND p.estado LIKE #{filtro.estadoSunat}

AND p.estado != '08'
AND p.estado != '05'
GROUP BY p.codigo_moneda
